import React from 'react';
import { Box, Typography, Card, CardMedia, CardContent, Button, Grid, Paper } from '@mui/material';

/***********************************************
@page ProductPage
@description 개별 상품 상세 페이지 (구매 페이지)
***********************************************/
const ProductPage = () => {
  // 하나의 상품 데이터 (예시 데이터)
  const item = {
    id: 1,
    title: "프리미엄 스니커즈",
    price: "$99.99",
    image: "https://images.pexels.com/photos/11721287/pexels-photo-1175287.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260",
    description: "이 프리미엄 스니커즈는 고급 가죽과 독특한 디자인으로 최고의 착용감을 제공합니다. 일상에서 편안하게 신을 수 있으며, 어떤 스타일에도 잘 어울립니다.",
  };

  return (
    <Box margin={2}>
      <Grid container spacing={4} justifyContent="center">
        <Grid item xs={12} sm={8} md={6}>
          <Paper elevation={3} sx={{ padding: 2 }}>
            {/* 상품 이미지 */}
            <Card>
              <CardMedia
                component="img"
                height="400"
                image={item.image}
                alt={item.title}
              />
              <CardContent>
                {/* 상품 제목 */}
                <Typography variant="h4" gutterBottom>
                  {item.title}
                </Typography>

                {/* 상품 가격 */}
                <Typography variant="h5" color="primary" gutterBottom>
                  {item.price}
                </Typography>

                {/* 상품 설명 */}
                <Typography variant="body1" paragraph>
                  {item.description}
                </Typography>

                {/* 구매 버튼 */}
                <Button variant="contained" color="primary" fullWidth size="large">
                  지금 구매하기
                </Button>
              </CardContent>
            </Card>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ProductPage;
