import React from 'react';
import { Card, CardContent, CardMedia, Typography, Grid, Box, Button } from '@mui/material';
import { styled } from '@mui/system';

/***********************************************
@page ImageCard 
@description 쇼핑몰 아이템 카드
***********************************************/

// 스타일 개선: 카드에 그림자 효과 추가
const StyledCard = styled(Card)(({ theme }) => ({
  transition: 'transform 0.3s ease, box-shadow 0.3s ease',
  '&:hover': {
    transform: 'scale(1.05)',
 // 직접 boxShadow 속성 설정
    boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.15)', // 예시로 custom boxShadow 사용
  },
}));

const ImageCard = () => {
  return (
    <Box margin={2}>
      <Grid container spacing={2} justifyContent="center">
        <Grid item xs={12} sm={6} md={4}>
          {/* StyledCard로 스타일링된 카드 사용 */}
          <StyledCard>
            <CardMedia
              component="img"
              height="200"
              image="https://via.placeholder.com/300"
              alt="Example Image"
            />
            <CardContent>
              {/* 제목 */}
              <Typography variant="h6" component="div" gutterBottom>
                쇼핑몰 아이템 제목
              </Typography>
              {/* 설명 */}
              <Typography variant="body2" color="text.secondary" paragraph>
                아이템 설명이 여기 들어갑니다. 이 곳에 아이템의 특징이나 사용법 등을 추가할 수 있습니다.
              </Typography>
              {/* 가격 */}
              <Typography variant="h6" color="primary" gutterBottom>
                가격: <strong>$99.99</strong>
              </Typography>
              {/* 구매 버튼 */}
              <Button variant="contained" color="primary" fullWidth>
                지금 구매하기
              </Button>
            </CardContent>
          </StyledCard>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ImageCard;
