import React from 'react';
import { Grid, Card, CardMedia, CardContent, Typography, Button, Box } from '@mui/material';
import { Link } from 'react-router-dom'; // Link 컴포넌트를 사용하여 라우팅 처리

/***********************************************
@page ShopPage 
@description 제품 목록 페이지
***********************************************/
const ShopPage = () => {
  const items = [
    { id: 1, title: "제품 1", price: "$20", image: "https://via.placeholder.com/150" },
    { id: 2, title: "제품 2", price: "$30", image: "https://via.placeholder.com/150" },
    { id: 3, title: "제품 3", price: "$40", image: "https://via.placeholder.com/150" },
  ];

  return (
    <Box margin={2}>
      <Grid container spacing={4} justifyContent="center">
        {items.map((item) => (
          <Grid item xs={12} sm={6} md={4} key={item.id}>
            <Card>
              <CardMedia
                component="img"
                height="200"
                image={item.image}
                alt={item.title}
              />
              <CardContent>
                <Typography variant="h6" component="div">
                  {item.title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {item.price}
                </Typography>
                {/* Link를 사용하여 상세 페이지로 이동 */}
                <Button
                  component={Link}
                  to={`/product/${item.id}`} // 상품 상세 페이지로 이동
                  variant="contained"
                  color="primary"
                  fullWidth
                >
                  상세보기
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default ShopPage;
