import React from 'react';
import { useParams } from 'react-router-dom';
import { Box, Typography, Card, CardMedia, CardContent, Button } from '@mui/material';

/***********************************************
@page ProductPage
@description 개별 상품 상세 페이지
***********************************************/
const ProductPage = () => {
  const { id } = useParams(); // URL에서 상품 ID 가져오기
  const items = [
    { id: 1, title: "제품 1", price: "$20", image: "https://via.placeholder.com/150", description: "이 제품은 매우 좋은 품질을 자랑합니다." },
    { id: 2, title: "제품 2", price: "$30", image: "https://via.placeholder.com/150", description: "이 제품은 최고급 재료로 만들어졌습니다." },
    { id: 3, title: "제품 3", price: "$40", image: "https://via.placeholder.com/150", description: "이 제품은 사용하기 매우 편리합니다." },
  ];

  // 상품 ID에 맞는 상세 정보 찾기
  const item = items.find((item) => item.id === parseInt(id));

  if (!item) {
    return <Typography variant="h6">상품을 찾을 수 없습니다.</Typography>;
  }

  return (
    <Box margin={2}>
      <Card>
        <CardMedia
          component="img"
          height="400"
          image={item.image}
          alt={item.title}
        />
        <CardContent>
          <Typography variant="h5" gutterBottom>
            {item.title}
          </Typography>
          <Typography variant="h6" color="primary" gutterBottom>
            {item.price}
          </Typography>
          <Typography variant="body1" paragraph>
            {item.description}
          </Typography>
          <Button variant="contained" color="primary" fullWidth>
            구매하기
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
};

export default ProductPage;
