import React from 'react';
import { Grid, Card, CardMedia, CardContent, Typography, Button } from '@mui/material';

const ShopPage = () => {
  const items = [
    { id: 1, title: "제품 1", price: "$20", image: "https://via.placeholder.com/150" },
    { id: 2, title: "제품 2", price: "$30", image: "https://via.placeholder.com/150" },
    { id: 3, title: "제품 3", price: "$40", image: "https://via.placeholder.com/150" },
  ];

  return (
    <Grid container spacing={4} justifyContent="center">
      {items.map((item) => (
        <Grid item xs={12} sm={6} md={4} key={item.id}>
          <Card>
            <CardMedia
              component="img"
              height="200"
              image={item.image}
              alt={item.title}
            />
            <CardContent>
              <Typography variant="h6" component="div">
                {item.title}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {item.price}
              </Typography>
              <Button variant="contained" color="primary" fullWidth>
                구매하기
              </Button>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

export default ShopPage;
